Blockly.Blocks['dev_tspressed'] = {
  /**
   * Block to detect if touch switch (ts) pressed. (触碰开关）
   * @this Blockly.Block
   */
  init: function() {
    this.jsonInit({
      "message0": "touch switch %1 pressed?",
      "args0": [
        {
          "type": "input_value",
          "name": "PORT"
        }
      ],
      "output": 'Boolean',
      "outputShape": Blockly.OUTPUT_SHAPE_HEXAGONAL,
      "checkboxInFlyout": false,
      "category": Blockly.Categories.dev,
      "colour": Blockly.Colours.dev.primary,
      "colourSecondary": Blockly.Colours.dev.secondary,
      "colourTertiary": Blockly.Colours.dev.tertiary
    });
  }
};

Blockly.Blocks['dev_sonar'] = {
  /**
   * Block to report ultrasonic sensor value. (超声波 Ultrasonic Sensor)
   * @this Blockly.Block
   */
  init: function() {
    this.jsonInit({
      "message0": "ultrasonic %1 distance",
      "args0": [
        {
          "type": "input_value",
          "name": "PORT"
        }
      ],
      "output": 'Number',
      "outputShape": Blockly.OUTPUT_SHAPE_ROUND,
      "checkboxInFlyout": true,
      "category": Blockly.Categories.dev,
      "colour": Blockly.Colours.dev.primary,
      "colourSecondary": Blockly.Colours.dev.secondary,
      "colourTertiary": Blockly.Colours.dev.tertiary
    });
  }
};

Blockly.Blocks['dev_port_menu'] = {
  /**
   * Drums drop-down menu.
   * @this Blockly.Block
   */
  init: function() {
    this.jsonInit(
      {
        "message0": "%1",
        "args0": [
          {
            "type": "field_dropdown",
            "name": "PORT",
            "options": [
              ['port1', '1'],
              ['port2', '2'],
              ['port3', '3'],
              ['port4', '4'],
              ['port5', '5'],
              ['port6', '6']
            ]
          }
        ],
        "inputsInline": true,
        "output": "String",
        "colour": Blockly.Colours.dev.secondary,
        "colourSecondary": Blockly.Colours.dev.secondary,
        "colourTertiary": Blockly.Colours.dev.tertiary,
        "outputShape": Blockly.OUTPUT_SHAPE_ROUND
      });
  }
};

Blockly.Blocks['dev_port_options'] = [
  ['port1', '1'],
  ['port2', '2']
];
