Blockly.Arduino['data_setvariableto'] = function(block) {
    var argument0 = Blockly.Arduino.valueToCode(block, 'VALUE',
        Blockly.Arduino.ORDER_ASSIGNMENT) || '0';

    var varName = Blockly.Arduino.variableDB_.getName(
        block.getFieldValue('VARIABLE'), Blockly.Variables.NAME_TYPE);

    // add variable
    Blockly.Arduino.addVariable(varName,
        'Data ' +
        Blockly.Arduino.variableDB_.getName(varName, Blockly.Variables.NAME_TYPE) + ';');

    return varName + ' = ' + argument0 + ';\n';
};

Blockly.Arduino['data_changevariableby'] = function(block) {
    var argument0 = Blockly.Arduino.valueToCode(block, 'VALUE',
        Blockly.Arduino.ORDER_ASSIGNMENT) || '0';

    var varName = Blockly.Arduino.variableDB_.getName(
        block.getFieldValue('VARIABLE'), Blockly.Variables.NAME_TYPE);

    // add variable
    Blockly.Arduino.addVariable(varName,
        'Data ' +
        Blockly.Arduino.variableDB_.getName(varName, Blockly.Variables.NAME_TYPE) + ';');

    return varName + ' = ' + varName + '+' + argument0 + ';\n';
};

Blockly.Arduino['data_variable'] = function(block) {
    var code = Blockly.Arduino.variableDB_.getName(block.getFieldValue('VARIABLE'),
        Blockly.Variables.NAME_TYPE);
    return [code, Blockly.Arduino.ORDER_ATOMIC];
};
